import React, { useMemo, useState } from 'react';
import './Alerts.css';
import DataTable from './DataTable';

export default function Alerts({ issues }) {
  const [tab, setTab] = useState('all');

  const tabs = [
    { id: 'all', label: 'All' },
    { id: 'critical', label: 'Critical' },
    { id: 'error', label: 'Error' },
    { id: 'warning', label: 'Warning' },
    { id: 'information', label: 'Info' },
  ];

  const filtered = useMemo(() => {
    if (tab === 'all') return issues || [];
    return (issues || []).filter(i => i.severity?.toLowerCase() === tab);
  }, [issues, tab]);

  const columns = [
    { key: 'severity', label: 'Severity', render: (v) => <span className={`badge badge-${String(v).toLowerCase()}`}>{v}</span> },
    { key: 'category', label: 'Category' },
    { key: 'description', label: 'Description' },
    { key: 'occurrences', label: 'Occurrences' },
  ];

  return (
    <div className="alerts">
      <div className="alerts-header">
        <h2>Alerts & Issues</h2>
        <div className="alerts-tabs">
          {tabs.map(t => (
            <button key={t.id} className={`alerts-tab ${tab === t.id ? 'active' : ''}`} onClick={() => setTab(t.id)}>
              {t.label}
            </button>
          ))}
        </div>
      </div>
      <DataTable columns={columns} rows={filtered} initialSort={{ key: 'occurrences', direction: 'desc' }} />
    </div>
  );
}
