import React from 'react';
import './Trends.css';

export default function Trends({ analysisResult }) {
  const hasData = Boolean(analysisResult?.analysis);
  return (
    <div className="trends">
      <div className="trends-header">
        <h2>Trends</h2>
        <div className="trends-actions">
          <button className="btn btn-secondary">Download Report</button>
        </div>
      </div>

      {!hasData && (
        <div className="empty-state">
          <div className="empty-icon">ðŸ“ˆ</div>
          <h2>No Trend Data</h2>
          <p>Run an analysis to generate trend metrics</p>
        </div>
      )}

      {hasData && (
        <div className="trends-grid">
          <div className="trend-card">
            <div className="trend-title">Issues Over Time</div>
            <div className="trend-chart-placeholder">Line Chart</div>
          </div>
          <div className="trend-card">
            <div className="trend-title">Severity Distribution</div>
            <div className="trend-chart-placeholder">Stacked Area</div>
          </div>
          <div className="trend-card">
            <div className="trend-title">Top Categories</div>
            <div className="trend-chart-placeholder">Bar Chart</div>
          </div>
        </div>
      )}
    </div>
  );
}
