import React, { useMemo, useState, useEffect } from 'react';
import './Alerts.css';
import DataTable from './DataTable';

export default function Alerts({ issues, sessions }) {
  const [tab, setTab] = useState('all');
  const [deviceFilter, setDeviceFilter] = useState('all');
  const [dateFrom, setDateFrom] = useState('');
  const [dateTo, setDateTo] = useState('');
  const [sortBy, setSortBy] = useState('occurrences'); // occurrences, severity, category
  const [sortOrder, setSortOrder] = useState('desc');
  const [searchQuery, setSearchQuery] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedIssue, setSelectedIssue] = useState(null);
  const [drawerUserSearch, setDrawerUserSearch] = useState('');
  const [drawerDeviceSearch, setDrawerDeviceSearch] = useState('');
  const itemsPerPage = 20;

  // Get unique devices from sessions
  const devices = useMemo(() => {
    const deviceSet = new Set();
    (sessions?.sessions || []).forEach(s => deviceSet.add(s.systemName));
    return Array.from(deviceSet).sort();
  }, [sessions]);

  // Get severity counts
  const severityCounts = useMemo(() => {
    return (issues || []).reduce((acc, issue) => {
      acc[issue.severity] = (acc[issue.severity] || 0) + 1;
      return acc;
    }, { Critical: 0, Error: 0, Warning: 0, Information: 0 });
  }, [issues]);

  const issuesWithImpacts = useMemo(() => {
    return (issues || []).map(issue => {
      const userImpact = Array.isArray(issue.affectedUsers) ? issue.affectedUsers.length : issue.userCount || 0;
      const deviceImpact = Array.isArray(issue.affectedSystems) ? issue.affectedSystems.length : (issue.affectedSystems ? issue.affectedSystems.length : 0);

      // derive earliest/latest timestamps for filtering
      const timestamps = (issue.samples || []).map(s => new Date(s.timestamp)).filter(d => !isNaN(d));
      const firstSeen = timestamps.length ? new Date(Math.min(...timestamps)) : null;
      const lastSeen = timestamps.length ? new Date(Math.max(...timestamps)) : null;

      return {
        ...issue,
        userImpact,
        deviceImpact,
        firstSeen,
        lastSeen,
      };
    });
  }, [issues]);

  // Filter and sort issues
  const filtered = useMemo(() => {
    let result = issuesWithImpacts;

    // Filter by severity tab
    if (tab !== 'all') {
      result = result.filter(i => i.severity?.toLowerCase() === tab);
    }

    // Filter by device
    if (deviceFilter !== 'all') {
      result = result.filter(i => (i.affectedSystems || []).includes(deviceFilter));
    }

    // Filter by date range (inclusive)
    if (dateFrom || dateTo) {
      const from = dateFrom ? new Date(dateFrom) : null;
      const to = dateTo ? new Date(dateTo) : null;
      result = result.filter(i => {
        if (!i.firstSeen && !i.lastSeen) return false;
        const start = i.firstSeen || i.lastSeen;
        const end = i.lastSeen || i.firstSeen;
        if (from && end < from) return false;
        if (to && start > to) return false;
        return true;
      });
    }

    // Filter by search query
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      result = result.filter(i => 
        i.description?.toLowerCase().includes(query) ||
        i.category?.toLowerCase().includes(query) ||
        i.severity?.toLowerCase().includes(query)
      );
    }

    // Sort
    result.sort((a, b) => {
      let comparison = 0;
      switch (sortBy) {
        case 'severity':
          const severityOrder = { Critical: 4, Error: 3, Warning: 2, Information: 1 };
          comparison = (severityOrder[a.severity] || 0) - (severityOrder[b.severity] || 0);
          break;
        case 'category':
          comparison = (a.category || '').localeCompare(b.category || '');
          break;
        case 'occurrences':
        default:
          comparison = (a.occurrences || 0) - (b.occurrences || 0);
      }
      return sortOrder === 'desc' ? -comparison : comparison;
    });

    return result;
  }, [issuesWithImpacts, tab, deviceFilter, searchQuery, sortBy, sortOrder, dateFrom, dateTo]);

  // Paginate
  const totalPages = Math.ceil(filtered.length / itemsPerPage);
  const paginatedIssues = useMemo(() => {
    const startIdx = (currentPage - 1) * itemsPerPage;
    return filtered.slice(startIdx, startIdx + itemsPerPage);
  }, [filtered, currentPage]);

  const tabs = [
    { id: 'all', label: 'All', count: (issues || []).length },
    { id: 'critical', label: 'Critical', count: severityCounts.Critical },
    { id: 'error', label: 'Error', count: severityCounts.Error },
    { id: 'warning', label: 'Warning', count: severityCounts.Warning },
    { id: 'information', label: 'Info', count: severityCounts.Information },
  ];

  const columns = [
    { key: 'severity', label: 'Severity', render: (v) => <span className={`badge badge-${String(v).toLowerCase()}`}>{v}</span> },
    { key: 'category', label: 'Category' },
    { key: 'description', label: 'Description' },
    { key: 'userImpact', label: 'Users Impacted' },
    { key: 'deviceImpact', label: 'Devices Impacted' },
    { key: 'occurrences', label: 'Occurrences' },
  ];

  const handleRowClick = (issue) => {
    if (!issue) return;
    setSelectedIssue(issue);
  };

  useEffect(() => {
    const onKeyDown = (e) => {
      if (e.key === 'Escape') {
        setSelectedIssue(null);
      }
    };
    window.addEventListener('keydown', onKeyDown);
    return () => window.removeEventListener('keydown', onKeyDown);
  }, []);

  return (
    <div className="alerts">
      <div className="alerts-header">
        <div>
          <h2>Alerts & Issues</h2>
          <p>Fleet-wide alert monitoring and issue tracking</p>
        </div>
        <div className="alerts-controls">
          {/* Search */}
          <div className="search-box">
            <span className="search-icon">üîç</span>
            <input
              type="text"
              placeholder="Search issues..."
              value={searchQuery}
              onChange={(e) => {
                setSearchQuery(e.target.value);
                setCurrentPage(1);
              }}
              className="search-input"
            />
          </div>

          {/* Device Filter */}
          <select
            value={deviceFilter}
            onChange={(e) => {
              setDeviceFilter(e.target.value);
              setCurrentPage(1);
            }}
            className="device-filter-select"
          >
            <option value="all">All Devices ({devices.length})</option>
            {devices.map(device => (
              <option key={device} value={device}>
                {device}
              </option>
            ))}
          </select>

          {/* Date filters */}
          <div className="date-filters">
            <label className="date-label">From</label>
            <input
              type="date"
              value={dateFrom}
              onChange={(e) => {
                setDateFrom(e.target.value);
                setCurrentPage(1);
              }}
              className="date-input"
            />
            <label className="date-label">To</label>
            <input
              type="date"
              value={dateTo}
              onChange={(e) => {
                setDateTo(e.target.value);
                setCurrentPage(1);
              }}
              className="date-input"
            />
          </div>

          {/* Sort By */}
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="device-filter-select"
          >
            <option value="occurrences">Sort: Occurrences</option>
            <option value="severity">Sort: Severity</option>
            <option value="category">Sort: Category</option>
          </select>

          {/* Sort Order */}
          <button
            className="sort-order-btn"
            onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
            title={sortOrder === 'asc' ? 'Ascending' : 'Descending'}
          >
            {sortOrder === 'asc' ? '‚Üë' : '‚Üì'}
          </button>
        </div>
      </div>

      {/* Severity Summary */}
      <div className="severity-summary">
        <div className="severity-card critical">
          <div className="severity-icon">üõë</div>
          <div className="severity-info">
            <div className="severity-count">{severityCounts.Critical}</div>
            <div className="severity-label">Critical</div>
          </div>
        </div>
        <div className="severity-card error">
          <div className="severity-icon">üî¥</div>
          <div className="severity-info">
            <div className="severity-count">{severityCounts.Error}</div>
            <div className="severity-label">Error</div>
          </div>
        </div>
        <div className="severity-card warning">
          <div className="severity-icon">‚ö†Ô∏è</div>
          <div className="severity-info">
            <div className="severity-count">{severityCounts.Warning}</div>
            <div className="severity-label">Warning</div>
          </div>
        </div>
        <div className="severity-card info">
          <div className="severity-icon">‚ÑπÔ∏è</div>
          <div className="severity-info">
            <div className="severity-count">{severityCounts.Information}</div>
            <div className="severity-label">Info</div>
          </div>
        </div>
      </div>

      {/* Tabs */}
      <div className="alerts-tabs">
        {tabs.map(t => (
          <button 
            key={t.id} 
            className={`alerts-tab ${tab === t.id ? 'active' : ''}`} 
            onClick={() => {
              setTab(t.id);
              setCurrentPage(1);
            }}
          >
            {t.label} <span className="tab-count">({t.count})</span>
          </button>
        ))}
      </div>

      {/* Results Info */}
      <div className="alerts-results-info">
        <span>Showing {paginatedIssues.length} of {filtered.length} issues</span>
        {filtered.length !== (issues || []).length && (
          <button
            className="clear-filters-btn"
            onClick={() => {
              setSearchQuery('');
              setDeviceFilter('all');
              setDateFrom('');
              setDateTo('');
              setCurrentPage(1);
            }}
          >
            Clear Filters
          </button>
        )}
      </div>

      {/* Table */}
      {paginatedIssues.length > 0 ? (
        <>
          <DataTable columns={columns} rows={paginatedIssues} onRowClick={handleRowClick} />

          {selectedIssue && (
            <>
              <div className="drawer-backdrop" onClick={() => setSelectedIssue(null)} />
              <aside className="issue-drawer">
                <div className="issue-drawer-header">
                  <div className="drawer-title-section">
                    <h3 className="drawer-title">Alerts & Issues Details</h3>
                    <div className={`badge badge-${String(selectedIssue.severity).toLowerCase()}`}>{selectedIssue.severity}</div>
                  </div>
                  <button className="close-btn" onClick={() => setSelectedIssue(null)}>‚úï</button>
                </div>

                <div className="issue-drawer-content">
                  <div className="drawer-section">
                    <h4 className="drawer-section-title">{selectedIssue.description}</h4>
                    <p className="issue-meta">Category: {selectedIssue.category} ‚Ä¢ Occurrences: {selectedIssue.occurrences}</p>
                  </div>

                  <div className="drawer-section">
                    <h5 className="section-heading">üìä Impact Summary</h5>
                    <div className="impact-stats">
                      <div className="impact-stat">
                        <span className="impact-number">{selectedIssue.userImpact}</span>
                        <span className="impact-label">Users Affected</span>
                      </div>
                      <div className="impact-stat">
                        <span className="impact-number">{selectedIssue.deviceImpact}</span>
                        <span className="impact-label">Devices Affected</span>
                      </div>
                    </div>
                  </div>

                  {selectedIssue.rootCause && selectedIssue.rootCause !== 'N/A' && (
                    <div className="drawer-section root-cause-section">
                      <h5 className="section-heading">üîç Root Cause Analysis</h5>
                      <div className="analysis-box">
                        <p>{selectedIssue.rootCause}</p>
                      </div>
                    </div>
                  )}

                  {selectedIssue.solution && selectedIssue.solution !== 'N/A' && (
                    <div className="drawer-section solution-section">
                      <h5 className="section-heading">üí° Recommended Solution</h5>
                      <div className="analysis-box solution-box">
                        <p>{selectedIssue.solution}</p>
                      </div>
                    </div>
                  )}

                  <div className="drawer-section">
                    <h5 className="section-heading">‚è±Ô∏è Timeline</h5>
                    <div className="timeline-info">
                      <p><strong>First Seen:</strong> {selectedIssue.firstSeen ? selectedIssue.firstSeen.toLocaleString() : 'Unknown'}</p>
                      <p><strong>Last Seen:</strong> {selectedIssue.lastSeen ? selectedIssue.lastSeen.toLocaleString() : 'Unknown'}</p>
                    </div>
                  </div>

                  {selectedIssue.affectedUsers && selectedIssue.affectedUsers.length > 0 && (
                    <div className="drawer-section">
                      <h5 className="section-heading">üë• Affected Users ({selectedIssue.affectedUsers.length})</h5>
                      <div className="list-search-box">
                        <span className="search-icon">üîç</span>
                        <input
                          type="text"
                          placeholder="Search users..."
                          value={drawerUserSearch}
                          onChange={(e) => setDrawerUserSearch(e.target.value)}
                          className="list-search-input"
                        />
                      </div>
                      <div className="scrollable-list">
                        {selectedIssue.affectedUsers
                          .filter(user => user.toLowerCase().includes(drawerUserSearch.toLowerCase()))
                          .map((user, idx) => (
                            <div key={idx} className="list-item">
                              <span className="list-icon">üë§</span>
                              <span className="list-text">{user}</span>
                            </div>
                          ))}
                      </div>
                    </div>
                  )}

                  {selectedIssue.affectedSystems && selectedIssue.affectedSystems.length > 0 && (
                    <div className="drawer-section">
                      <h5 className="section-heading">üíª Affected Devices ({selectedIssue.affectedSystems.length})</h5>
                      <div className="list-search-box">
                        <span className="search-icon">üîç</span>
                        <input
                          type="text"
                          placeholder="Search devices..."
                          value={drawerDeviceSearch}
                          onChange={(e) => setDrawerDeviceSearch(e.target.value)}
                          className="list-search-input"
                        />
                      </div>
                      <div className="scrollable-list">
                        {selectedIssue.affectedSystems
                          .filter(device => device.toLowerCase().includes(drawerDeviceSearch.toLowerCase()))
                          .map((device, idx) => (
                            <div key={idx} className="list-item">
                              <span className="list-icon">üñ•Ô∏è</span>
                              <span className="list-text">{device}</span>
                            </div>
                          ))}
                      </div>
                    </div>
                  )}

                  {selectedIssue.samples && selectedIssue.samples.length > 0 && (
                    <div className="drawer-section">
                      <h5 className="section-heading">üìù Sample Events</h5>
                      <div className="samples-list">
                        {selectedIssue.samples.slice(0, 5).map((s, idx) => (
                          <div key={idx} className="sample-item">
                            <div className="sample-meta">{new Date(s.timestamp).toLocaleString()} ‚Ä¢ {s.system} ‚Ä¢ {s.user}</div>
                            <div className="sample-text">{s.message || 'Log entry'}</div>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </aside>
            </>
          )}
          
          {/* Pagination */}
          {totalPages > 1 && (
            <div className="pagination-controls">
              <button
                className="pagination-btn"
                onClick={() => setCurrentPage(1)}
                disabled={currentPage === 1}
              >
                ‚ü®‚ü® First
              </button>
              <button
                className="pagination-btn"
                onClick={() => setCurrentPage(currentPage - 1)}
                disabled={currentPage === 1}
              >
                ‚ü® Previous
              </button>
              <div className="pagination-info">
                Page {currentPage} of {totalPages}
              </div>
              <button
                className="pagination-btn"
                onClick={() => setCurrentPage(currentPage + 1)}
                disabled={currentPage === totalPages}
              >
                Next ‚ü©
              </button>
              <button
                className="pagination-btn"
                onClick={() => setCurrentPage(totalPages)}
                disabled={currentPage === totalPages}
              >
                Last ‚ü©‚ü©
              </button>
            </div>
          )}
        </>
      ) : (
        <div className="empty-state">
          <span className="empty-icon">‚úÖ</span>
          <h3>No issues found</h3>
          <p>Try adjusting your search or filters</p>
        </div>
      )}
    </div>
  );
}
