import React from 'react';
import './Sidebar.css';

function Sidebar({ activeView, onViewChange }) {
  const menuItems = [
    { id: 'dashboard', icon: 'üìä', label: 'Dashboard' },
    { id: 'systems', icon: 'üñ•Ô∏è', label: 'Systems' },
    { id: 'devices', icon: 'üíª', label: 'Devices' },
    { id: 'applications', icon: 'üß©', label: 'Applications' },
    { id: 'users', icon: 'üë•', label: 'Users' },
    { id: 'locations', icon: 'üó∫Ô∏è', label: 'Locations' },
    { id: 'network', icon: 'üåê', label: 'Network' },
    { id: 'bootlogon', icon: '‚è±Ô∏è', label: 'Boot & Logon' },
    { id: 'stability', icon: 'üõ°Ô∏è', label: 'App Stability' },
    { id: 'synthetics', icon: 'üß™', label: 'Synthetics' },
    { id: 'alerts', icon: 'üö®', label: 'Alerts' },
    { id: 'trends', icon: 'üìâ', label: 'Trends' },
    { id: 'reports', icon: 'üìù', label: 'Reports' },
    { id: 'analyze', icon: 'üî¨', label: 'Run Analysis' },
    { id: 'issues', icon: '‚ö†Ô∏è', label: 'Issues' },
    { id: 'analytics', icon: 'üìà', label: 'Analytics' },
  ];

  return (
    <aside className="sidebar">
      <nav className="sidebar-nav">
        {menuItems.map(item => (
          <button
            key={item.id}
            className={`nav-item ${activeView === item.id ? 'active' : ''}`}
            onClick={() => onViewChange(item.id)}
          >
            <span className="nav-icon">{item.icon}</span>
            <span className="nav-label">{item.label}</span>
          </button>
        ))}
      </nav>
    </aside>
  );
}

export default Sidebar;
