import React from 'react';
import './Sidebar.css';

function Sidebar({ activeView, onViewChange }) {
  const menuItems = [
    // Core Monitoring
    { id: 'dashboard', icon: 'ðŸ“Š', label: 'Dashboard', group: 'Core' },
    { id: 'alerts', icon: 'ðŸš¨', label: 'Alerts', group: 'Core' },
    { id: 'trends', icon: 'ðŸ“ˆ', label: 'Trends', group: 'Core' },
    
    // Entities
    { id: 'systems', icon: 'ðŸ–¥ï¸', label: 'Systems', group: 'Entities' },
    { id: 'users', icon: 'ðŸ‘¥', label: 'Users', group: 'Entities' },
    { id: 'applications', icon: 'ðŸ§©', label: 'Applications', group: 'Entities' },
    
    // Experience
    { id: 'network', icon: 'ðŸŒ', label: 'Network', group: 'Experience' },
    { id: 'bootlogon', icon: 'â±ï¸', label: 'Boot & Logon', group: 'Experience' },
    
    // Admin
    { id: 'analyze', icon: 'ðŸ”¬', label: 'Run Analysis', group: 'Admin' },
    { id: 'reports', icon: 'ðŸ“', label: 'Reports', group: 'Admin' },
  ];

  // Group items by section
  const groups = {
    Core: menuItems.filter(i => i.group === 'Core'),
    Entities: menuItems.filter(i => i.group === 'Entities'),
    Experience: menuItems.filter(i => i.group === 'Experience'),
    Admin: menuItems.filter(i => i.group === 'Admin'),
  };

  return (
    <aside className="sidebar">
      <nav className="sidebar-nav">
        {Object.entries(groups).map(([groupName, items]) => (
          items.length > 0 && (
            <div key={groupName} className="nav-group">
              <div className="nav-group-label">{groupName}</div>
              {items.map(item => (
                <button
                  key={item.id}
                  className={`nav-item ${activeView === item.id ? 'active' : ''}`}
                  onClick={() => onViewChange(item.id)}
                >
                  <span className="nav-icon">{item.icon}</span>
                  <span className="nav-label">{item.label}</span>
                </button>
              ))}
            </div>
          )
        ))}
      </nav>
    </aside>
  );
}

export default Sidebar;
