import React, { useState, useEffect, useCallback } from 'react';
import './Admin.css';
import { API_BASE_URL } from '../services/api';

// Helper function to construct full API URLs
const getApiUrl = (endpoint) => {
  const baseUrl = API_BASE_URL.replace(/\/api$/, ''); // Remove /api suffix if present
  return `${baseUrl}/api${endpoint}`;
};

export default function Admin({ config, onConfigUpdate, isAnalyzing, onAnalysisAction, onInMemoryDataCleared, onRefreshSessions }) {
  const [llmModel, setLlmModel] = useState('');
  const [llmProvider, setLlmProvider] = useState('');
  const [llmEnabled, setLlmEnabled] = useState(false);
  const [llmTemperature, setLlmTemperature] = useState(0.7);
  const [availableModels, setAvailableModels] = useState([]);
  const [installedModels, setInstalledModels] = useState([]);
  const [analyzerStatus, setAnalyzerStatus] = useState('idle');
  const [lastConfigUpdate, setLastConfigUpdate] = useState(null);
  const [autoAnalysisEnabled, setAutoAnalysisEnabled] = useState(false);
  const [analysisInterval, setAnalysisInterval] = useState(3600); // seconds
  const [logRetentionDays, setLogRetentionDays] = useState(30);
  const [maxThreads, setMaxThreads] = useState(4);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isDownloading, setIsDownloading] = useState(false);
  const [isDownloading70b, setIsDownloading70b] = useState(false);
  const [ollamaStatus, setOllamaStatus] = useState({ is_running: false });
  const [modelToDownload, setModelToDownload] = useState('');
  const [ollamaInstalled, setOllamaInstalled] = useState({ is_installed: false });
  const [isStartingOllama, setIsStartingOllama] = useState(false);
  const [isInstallingOllama, setIsInstallingOllama] = useState(false);
  const [isClearingMemory, setIsClearingMemory] = useState(false);
  const [useInstructionFile, setUseInstructionFile] = useState(true);
  const [instructionStatus, setInstructionStatus] = useState(null);
  const [instructionUploadFile, setInstructionUploadFile] = useState(null);
  const [isUploadingInstruction, setIsUploadingInstruction] = useState(false);
  const [isUpdatingInstructionToggle, setIsUpdatingInstructionToggle] = useState(false);
  const [logFolderFiles, setLogFolderFiles] = useState([]);
  const [isUploadingLogFolder, setIsUploadingLogFolder] = useState(false);

  // Initialize from config
  useEffect(() => {
    if (config) {
      setLlmModel(config.llm_model || 'llama3.1:70b');
      setLlmProvider(config.llm_provider || 'ollama');
      setLlmEnabled(config.llm_enabled !== false);
      setUseInstructionFile(config.use_instruction_file !== false);
      setLlmTemperature(config.llm_temperature || 0.7);
      setAutoAnalysisEnabled(config.auto_analysis_enabled !== false);
      setAnalysisInterval(config.analysis_interval || 3600);
      setLogRetentionDays(config.log_retention_days || 30);
      setMaxThreads(config.max_threads || 4);
    }
  }, [config]);

  const loadModels = useCallback(async (provider) => {
    try {
      const response = await fetch(getApiUrl(`/models/available?provider=${encodeURIComponent(provider)}`));
      if (response.ok) {
        const data = await response.json();
        setAvailableModels(data.models || []);
        setInstalledModels(data.installed_models || data.models || []);
      } else {
        setAvailableModels([]);
        setInstalledModels([]);
      }
    } catch (error) {
      console.error('Failed to fetch models:', error);
      setAvailableModels([]);
      setInstalledModels([]);
    }
  }, []);

  // Fetch available models when provider changes
  useEffect(() => {
    if (llmProvider) {
      loadModels(llmProvider);
    }
  }, [llmProvider, loadModels]);

  // Fetch analyzer status (initial + refresh)
  const fetchAnalyzerStatus = async () => {
    try {
      const response = await fetch(getApiUrl('/analyzer/status'));
      if (response.ok) {
        const data = await response.json();
        const status = data.status || (data.is_running ? 'running' : 'stopped');
        setAnalyzerStatus(status);
        if (data.last_error) {
          setErrorMessage(`âŒ Last analyzer error: ${data.last_error}`);
        } else {
          setErrorMessage('');
        }
      } else {
        setAnalyzerStatus('error');
        setErrorMessage('âŒ Failed to fetch analyzer status');
      }
    } catch (error) {
      setAnalyzerStatus('error');
      setErrorMessage(`âŒ Error: ${error.message}`);
    }
  };

  const fetchInstructionStatus = async () => {
    try {
      const response = await fetch(getApiUrl('/instructions/status'));
      if (response.ok) {
        const data = await response.json();
        if (data.instruction) {
          setInstructionStatus(data.instruction);
          setUseInstructionFile(data.instruction.enabled !== false);
        }
      }
    } catch (error) {
      console.error('Failed to fetch instruction status:', error);
    }
  };

  const fetchOllamaStatus = async () => {
    try {
      const response = await fetch(getApiUrl('/ollama/status'));
      const contentType = response.headers.get('content-type');
      
      if (!contentType || !contentType.includes('application/json')) {
        console.error('Non-JSON response from /api/ollama/status');
        setOllamaStatus({ is_running: false, error: 'Server returned invalid response' });
        return;
      }
      
      if (response.ok) {
        const data = await response.json();
        setOllamaStatus(data);
      } else {
        setOllamaStatus({ is_running: false, error: 'Failed to fetch status' });
      }
    } catch (error) {
      console.error('Failed to fetch Ollama status:', error);
      setOllamaStatus({ is_running: false, error: error.message });
    }
  };

  const checkOllamaInstalled = async () => {
    try {
      const response = await fetch(getApiUrl('/ollama/check-installed'));
      const contentType = response.headers.get('content-type');
      
      if (!contentType || !contentType.includes('application/json')) {
        console.error('Non-JSON response from /api/ollama/check-installed');
        setOllamaInstalled({ is_installed: false, error: 'Server returned invalid response' });
        return;
      }
      
      if (response.ok) {
        const data = await response.json();
        setOllamaInstalled(data);
      }
    } catch (error) {
      console.error('Failed to check Ollama installation:', error);
      setOllamaInstalled({ is_installed: false, error: error.message });
    }
  };

  useEffect(() => {
    fetchAnalyzerStatus();
    fetchOllamaStatus();
    checkOllamaInstalled();
    fetchInstructionStatus();
  }, []);

  const handleSaveConfiguration = async () => {
    setErrorMessage('');
    setSuccessMessage('');
    try {
      const response = await fetch(getApiUrl('/config'), {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          llm_model: llmModel,
          llm_provider: llmProvider,
          llm_enabled: llmEnabled,
          use_instruction_file: useInstructionFile,
          llm_temperature: parseFloat(llmTemperature),
          auto_analysis_enabled: autoAnalysisEnabled,
          analysis_interval: parseInt(analysisInterval),
          log_retention_days: parseInt(logRetentionDays),
          max_threads: parseInt(maxThreads),
        }),
      });

      if (response.ok) {
        const data = await response.json();
        setSuccessMessage('âœ… Configuration saved successfully!');
        setLastConfigUpdate(new Date().toLocaleString());
        setTimeout(() => setSuccessMessage(''), 3000);
        if (onConfigUpdate) onConfigUpdate(data);
        fetchInstructionStatus();
      } else {
        setErrorMessage('âŒ Failed to save configuration');
      }
    } catch (error) {
      setErrorMessage(`âŒ Error: ${error.message}`);
    }
  };

  const handleAnalyzerAction = async (action) => {
    setErrorMessage('');
    setSuccessMessage('');
    setAnalyzerStatus('processing');

    try {
      const response = await fetch(getApiUrl(`/analyzer/${action}`), {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });

      const data = await response.json().catch(() => ({}));

      if (response.ok) {
        const status = data.status || (action === 'start' ? 'running' : action === 'stop' ? 'stopped' : 'restarting');
        const message = data.message || `Analyzer ${action}ed successfully!`;
        setAnalyzerStatus(status);
        setSuccessMessage(`âœ… ${message}`);
        setTimeout(() => setSuccessMessage(''), 3000);
      } else if (response.status === 409) {
        const detail = data.error || 'Analyzer is already in the requested state';
        const fallbackStatus = action === 'start' ? 'running' : 'stopped';
        setAnalyzerStatus(fallbackStatus);
        setSuccessMessage(`â„¹ï¸ ${detail}`);
        setTimeout(() => setSuccessMessage(''), 3000);
      } else {
        const detail = data.error || `Failed to ${action} analyzer`;
        setAnalyzerStatus('error');
        setErrorMessage(`âŒ ${detail}`);
      }

      fetchAnalyzerStatus();
    } catch (error) {
      setAnalyzerStatus('error');
      setErrorMessage(`âŒ Error: ${error.message}`);
    }
  };

  const handleClearInMemoryData = async () => {
    setErrorMessage('');
    setSuccessMessage('');
    setIsClearingMemory(true);

    try {
      const response = await fetch(getApiUrl('/analyzer/clear-memory'), {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });

      const data = await response.json().catch(() => ({}));

      if (response.ok && data.success) {
        setSuccessMessage('âœ… In-memory analysis data cleared. Dashboard will refresh with current backend state.');
        if (onInMemoryDataCleared) {
          await onInMemoryDataCleared();
        }
        setTimeout(() => setSuccessMessage(''), 4000);
      } else {
        setErrorMessage(`âŒ ${data.error || 'Failed to clear in-memory data'}`);
      }

      fetchAnalyzerStatus();
    } catch (error) {
      setErrorMessage(`âŒ Error: ${error.message}`);
    } finally {
      setIsClearingMemory(false);
    }
  };

  const handleInstructionToggle = async () => {
    setErrorMessage('');
    setSuccessMessage('');
    setIsUpdatingInstructionToggle(true);

    try {
      const response = await fetch(getApiUrl('/instructions/toggle'), {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ enabled: useInstructionFile })
      });

      const data = await response.json().catch(() => ({}));
      if (response.ok && data.success) {
        setInstructionStatus(data.instruction || null);
        setSuccessMessage('âœ… Instruction file mode updated.');
        setTimeout(() => setSuccessMessage(''), 3000);
      } else {
        setErrorMessage(`âŒ ${data.error || 'Failed to update instruction mode'}`);
      }
    } catch (error) {
      setErrorMessage(`âŒ Error: ${error.message}`);
    } finally {
      setIsUpdatingInstructionToggle(false);
    }
  };

  const handleInstructionUpload = async () => {
    if (!instructionUploadFile) {
      setErrorMessage('âŒ Please choose an instruction file to upload.');
      return;
    }

    setErrorMessage('');
    setSuccessMessage('');
    setIsUploadingInstruction(true);

    try {
      const formData = new FormData();
      formData.append('file', instructionUploadFile);

      const response = await fetch(getApiUrl('/instructions/upload'), {
        method: 'POST',
        body: formData
      });

      const data = await response.json().catch(() => ({}));
      if (response.ok && data.success) {
        setInstructionStatus(data.instruction || null);
        setUseInstructionFile(true);
        setInstructionUploadFile(null);
        setSuccessMessage('âœ… Instruction file uploaded and enabled successfully.');
        setTimeout(() => setSuccessMessage(''), 4000);
      } else {
        setErrorMessage(`âŒ ${data.error || 'Failed to upload instruction file'}`);
      }
    } catch (error) {
      setErrorMessage(`âŒ Error: ${error.message}`);
    } finally {
      setIsUploadingInstruction(false);
    }
  };

  const handleLogFolderUpload = async () => {
    if (!logFolderFiles || logFolderFiles.length === 0) {
      setErrorMessage('âŒ Please select a log folder to upload.');
      return;
    }

    setErrorMessage('');
    setSuccessMessage('');
    setIsUploadingLogFolder(true);

    try {
      const formData = new FormData();

      Array.from(logFolderFiles).forEach((file) => {
        formData.append('files', file);
        formData.append('relative_paths', file.webkitRelativePath || file.name);
      });

      const response = await fetch(getApiUrl('/logs/upload-folder'), {
        method: 'POST',
        body: formData
      });

      const data = await response.json().catch(() => ({}));
      if (response.ok && data.success) {
        setSuccessMessage(`âœ… ${data.message}`);
        setTimeout(() => setSuccessMessage(''), 4000);
        setLogFolderFiles([]);
        if (onRefreshSessions) {
          await onRefreshSessions();
        }
      } else {
        setErrorMessage(`âŒ ${data.error || 'Failed to upload log folder'}`);
      }
    } catch (error) {
      setErrorMessage(`âŒ Error: ${error.message}`);
    } finally {
      setIsUploadingLogFolder(false);
    }
  };

  const handleRefreshSessions = async () => {
    setErrorMessage('');
    setSuccessMessage('');

    try {
      if (onRefreshSessions) {
        await onRefreshSessions();
      }
      await fetchInstructionStatus();
      await fetchAnalyzerStatus();
      setSuccessMessage('âœ… Sessions and dashboard data refreshed.');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (error) {
      setErrorMessage(`âŒ Failed to refresh sessions: ${error.message}`);
    }
  };

  const handleTestLLM = async () => {
    setErrorMessage('');
    setSuccessMessage('');
    try {
      const response = await fetch(getApiUrl('/test-llm'), {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          model: llmModel,
          provider: llmProvider,
        }),
      });

      if (response.ok) {
        setSuccessMessage('âœ… LLM connection test successful!');
        setTimeout(() => setSuccessMessage(''), 3000);
      } else {
        setErrorMessage('âŒ LLM connection test failed');
      }
    } catch (error) {
      setErrorMessage(`âŒ Error: ${error.message}`);
    }
  };

  const handleDownloadAndAnalyze = async () => {
    if (!onAnalysisAction) return;
    if (!llmModel) {
      setErrorMessage('âŒ Please select a model to download.');
      return;
    }

    setErrorMessage('');
    setSuccessMessage('');
    setIsDownloading(true);

    try {
      await onAnalysisAction({
        useLlm: true,
        model: llmModel,
        provider: llmProvider,
        autoDownload: true
      });

      setSuccessMessage('âœ… Model download initiated (if needed) and analysis started.');
      setTimeout(() => setSuccessMessage(''), 4000);
      loadModels(llmProvider);
    } catch (error) {
      setErrorMessage(`âŒ ${error.message || 'Failed to download model and analyze.'}`);
    } finally {
      setIsDownloading(false);
    }
  };

  const handleDownloadModel = async () => {
    if (!modelToDownload) {
      setErrorMessage('âŒ Please select a model to download.');
      return;
    }

    setErrorMessage('');
    setSuccessMessage('');
    setIsDownloading(true);

    try {
      const response = await fetch(getApiUrl('/ollama/pull'), {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ model: modelToDownload }),
      });

      const data = await response.json();

      if (response.ok) {
        setSuccessMessage(`âœ… Model '${modelToDownload}' downloaded successfully!`);
        setTimeout(() => setSuccessMessage(''), 4000);
        setModelToDownload('');
        fetchOllamaStatus();
        loadModels(llmProvider);
      } else {
        setErrorMessage(`âŒ ${data.error || 'Failed to download model'}`);
      }
    } catch (error) {
      setErrorMessage(`âŒ ${error.message || 'Failed to download model.'}`);
    } finally {
      setIsDownloading(false);
    }
  };

  const handleDownload70bModel = async () => {
    setErrorMessage('');
    setSuccessMessage('');
    setIsDownloading70b(true);

    try {
      const response = await fetch(getApiUrl('/ollama/pull'), {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ model: 'llama3.1:70b' }),
      });

      const data = await response.json();

      if (response.ok) {
        setSuccessMessage('âœ… llama3.1:70b downloaded successfully! Best model for Windows log analysis. Now set it as your default model above.');
        setTimeout(() => setSuccessMessage(''), 5000);
        setLlmModel('llama3.1:70b'); // Auto-select after download
        fetchOllamaStatus();
        loadModels(llmProvider);
      } else {
        setErrorMessage(`âŒ ${data.error || 'Failed to download llama3.1:70b'}`);
      }
    } catch (error) {
      setErrorMessage(`âŒ ${error.message || 'Failed to download model.'}`);
    } finally {
      setIsDownloading70b(false);
    }
  };

  const handleStartOllama = async () => {
    setErrorMessage('');
    setSuccessMessage('');
    setIsStartingOllama(true);

    try {
      const response = await fetch(getApiUrl('/ollama/start'), {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });

      const data = await response.json();

      if (data.is_running) {
        setSuccessMessage('âœ… Ollama started successfully!');
        setTimeout(() => setSuccessMessage(''), 3000);
        fetchOllamaStatus();
      } else if (data.started) {
        setSuccessMessage('â³ Ollama is starting... Please wait a moment and refresh status.');
        setTimeout(() => {
          fetchOllamaStatus();
          setSuccessMessage('');
        }, 3000);
      } else if (data.is_installed === false) {
        setErrorMessage(`âŒ Ollama is not installed. Click the download button below to get it.`);
      } else {
        setErrorMessage(`âŒ ${data.error || 'Failed to start Ollama'}`);
      }
    } catch (error) {
      setErrorMessage(`âŒ ${error.message || 'Failed to start Ollama.'}`);
    } finally {
      setIsStartingOllama(false);
    }
  };

  const handleInstallOllama = async () => {
    setErrorMessage('');
    setSuccessMessage('');
    setIsInstallingOllama(true);

    try {
      const response = await fetch(getApiUrl('/ollama/install'), {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });

      // Check if response is JSON
      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        const text = await response.text();
        console.error('Non-JSON response:', text);
        setErrorMessage(`âŒ Server error: ${text.substring(0, 100)}`);
        setIsInstallingOllama(false);
        return;
      }

      const data = await response.json();

      if (data.is_installed) {
        setSuccessMessage('âœ… Ollama installed successfully! Now click "Start Ollama" to run it.');
        setTimeout(() => setSuccessMessage(''), 5000);
        // Refresh installation status
        setTimeout(() => {
          checkOllamaInstalled();
        }, 2000);
      } else if (data.installing) {
        setSuccessMessage('â³ Ollama installation started. A window will open to complete setup. Please close it when done and click "Refresh Status".');
      } else {
        setErrorMessage(`âŒ ${data.error || 'Failed to install Ollama'}`);
      }
    } catch (error) {
      console.error('Installation error:', error);
      setErrorMessage(`âŒ ${error.message || 'Failed to install Ollama.'}`);
    } finally {
      setIsInstallingOllama(false);
    }
  };

  const analyzerStatusColor = {
    idle: '#94a3b8',
    running: '#10b981',
    stopped: '#ea580c',
    error: '#dc2626',
    processing: '#f59e0b',
    restarting: '#3b82f6',
  };

  const supportsDownload = llmProvider === 'ollama';
