            {analyzerStatus.toUpperCase()}
          </div>
        </div>
      </div>

      {/* Messages */}
      {successMessage && <div className="alert alert-success">{successMessage}</div>}
      {errorMessage && <div className="alert alert-error">{errorMessage}</div>}

      <div className="admin-grid">
        {/* LLM Configuration */}
        <div className="admin-card">
          <div className="card-header">
            <h3>ðŸ¤– LLM Model Configuration</h3>
            <span className="card-badge">AI Analysis</span>
          </div>
          <div className="card-content">
            <div className="form-group">
              <label>Enable LLM Analysis</label>
              <div className="toggle-switch">
                <input
                  type="checkbox"
                  checked={llmEnabled}
                  onChange={(e) => setLlmEnabled(e.target.checked)}
                  id="llm-toggle"
                />
                <label htmlFor="llm-toggle" className="toggle-label">
                  {llmEnabled ? 'Enabled' : 'Disabled'}
                </label>
              </div>
            </div>

            <div className="form-group">
              <label>LLM Provider</label>
              <select
                value={llmProvider}
                onChange={(e) => setLlmProvider(e.target.value)}
                className="form-input"
              >
                <option value="ollama">Ollama</option>
                <option value="openai">OpenAI</option>
                <option value="anthropic">Anthropic</option>
              </select>
            </div>

            <div className="form-group">
              <label>Model</label>
              <select
                value={llmModel}
                onChange={(e) => setLlmModel(e.target.value)}
                className="form-input"
              >
                <option value="">Select a model...</option>
                <option value="llama3.1:70b">â­ llama3.1:70b (Recommended - Best Accuracy)</option>
                <option value="llama3.2:3b">llama3.2:3b (default)</option>
                <option value="mistral">mistral</option>
                {availableModels.map((model) => (
                  <option key={model} value={model}>
                    {model}
                  </option>
                ))}
              </select>
            </div>

            {llmEnabled && supportsDownload && isModelSelected && !isModelAvailable && (
              <div className="alert alert-warning">
                <span>âš ï¸ Selected model is not installed locally.</span>
                <button
                  className="btn btn-primary"
                  onClick={handleDownloadAndAnalyze}
                  disabled={isDownloading || isAnalyzing}
                >
                  {isDownloading ? 'â¬‡ï¸ Downloading...' : 'â¬‡ï¸ Download & Analyze Now'}
                </button>
              </div>
            )}

            <div className="form-group">
              <label>Temperature (Creativity)</label>
              <div className="slider-container">
