import React, { useState } from 'react';
import { exportReport } from '../services/api';

export default function ReportsView() {
  const [status, setStatus] = useState('');

  const handleExport = async (format) => {
    try {
      setStatus(`Exporting ${format.toUpperCase()}...`);
      const res = await exportReport(format);
      if (res?.success) {
        setStatus(`Exported ${format.toUpperCase()} (${res.filename || 'ready'})`);
      } else {
        setStatus(`Export failed: ${res?.error || 'Unknown error'}`);
      }
    } catch (e) {
      setStatus(`Export failed: ${e.message}`);
    }
  };
  return (
    <div>
      <h2>Fleet Reports</h2>
      <div className="trends-grid" style={{ marginTop: 12 }}>
        <div className="trend-card">
          <div className="trend-title">Device Fleet Report</div>
          <div style={{ padding: 12 }}>
            <div className="btn-group">
              <button className="btn btn-secondary" onClick={() => handleExport('json')}>Export JSON</button>
              <button className="btn btn-secondary" onClick={() => handleExport('html')}>Export HTML</button>
              <button className="btn btn-secondary" onClick={() => handleExport('csv')}>Export CSV</button>
            </div>
            {status && <div style={{ marginTop: 8, color: '#9cc' }}>{status}</div>}
          </div>
        </div>
        <div className="trend-card">
          <div className="trend-title">Scheduled Reports</div>
          <div className="trend-chart-placeholder">Create weekly/monthly schedules</div>
        </div>
        <div className="trend-card">
          <div className="trend-title">Subscriptions</div>
          <div className="trend-chart-placeholder">Manage recipients</div>
        </div>
      </div>
      <div className="empty-state" style={{ marginTop: 12 }}>
        <div className="empty-icon">üìù</div>
        <h3>Hook these actions to backend /api/reports/export</h3>
        <p>We can wire buttons to call the export endpoint with desired format</p>
      </div>
    </div>
  );
}
